package com.example.compose.rallyimport androidx.compose.ui.platform.LocalContextimport androidx.compose.ui.test.assertIsDisplayedimport androidx.compose.ui.test.junit4.createComposeRuleimport androidx.compose.ui.test.onNodeWithContentDescriptionimport androidx.compose.ui.test.performClickimport androidx.compose.ui.test.performScrollToimport androidx.navigation.NavHostControllerimport androidx.navigation.compose.ComposeNavigatorimport androidx.navigation.testing.TestNavHostControllerimport org.junit.Assert.assertEqualsimport org.junit.Beforeimport org.junit.Ruleimport org.junit.Test/** * Created by JeongJaeHwan on 2022/12/14 **/class NavigationTest {  @get:Rule  val composeTestRule = createComposeRule()  lateinit var navHostController: NavHostController  @Before  fun setRallyNavHost() {    composeTestRule.setContent {      navHostController =        TestNavHostController(LocalContext.current)      navHostController.navigatorProvider.addNavigator(        ComposeNavigator()      )      RallyNavHost(navController = navHostController)    }  }  @Test  fun rallyNavHost_verifyOverviewStartDestination() {    composeTestRule      .onNodeWithContentDescription("Overview Screen")      .assertIsDisplayed()  }  @Test  fun rallyNavHost_clickAccount_navigatesToAccounts() {    composeTestRule      .onNodeWithContentDescription("All Accounts")      .performClick()    composeTestRule      .onNodeWithContentDescription("Accounts Screen")      .assertIsDisplayed()  }  @Test  fun rallyNavHost_clickAllBills_navigateToBills() {    composeTestRule      .onNodeWithContentDescription("All Bills")      .performScrollTo()      .performClick()    val route = navHostController.currentBackStackEntry?.destination?.route    assertEquals(route, "bills")  }}